import React, { useState } from 'react';
import { Container, Typography, Button, Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import SignUpForm from './SignUpForm';
import SignInForm from './SignInForm';
import {  toast } from 'react-toastify';

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(4),
  },
  toggleButton: {
    marginTop: theme.spacing(2),
  },
}));

const AuthPage = ({ setIsAuthenticated, setUser }) => {
  const classes = useStyles();
  const [isSignUp, setIsSignUp] = useState(false);

  const handleSignUp = async (formData) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_PROFILE_API_HOST}:3003/api/signup`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      console.log(data.message);
      toast.success('Sign up successful. Please sign in.', {
        position: "top-right",
        autoClose: 3000,
      });
        setIsSignUp(false);
    } catch (error) {
      console.error('Error signing up:', error);
      toast.error('Error signing up. Please try again.', {
        position: "top-right",
        autoClose: 3000,
      });  
    }
  };

  const handleSignIn = async (formData) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_PROFILE_API_HOST}:3003/api/signin`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      // Check if the response was successful
      if (response.ok) {
        const data = await response.json();
        console.log(data.message);

        // Assuming your server responds with a token and user only if credentials are correct
        if (data.token && data.user) {
          // Store the token and user object only if authentication was successful
          localStorage.setItem('token', data.token);
          localStorage.setItem('user', JSON.stringify(data.user));
          setIsAuthenticated(true);
          setUser(data.user);
          // Redirect to the home page after successful sign-in
          window.location.href = '/';
        } else {
          // Handle cases where the server response format is unexpected
          console.error('Unexpected server response format');
          toast.error('Unexpected server response format.', {
            position: "top-right",
            autoClose: 3000,
          });    
        }
      } else {
        // Handle unsuccessful sign-in attempts here
        // For example, show an error message to the user
        console.error('Sign-in failed. Please check your credentials.');
        toast.error('Sign-in failed. Please check your credentials.', {
            position: "top-right",
            autoClose: 3000,
          });
      }
    } catch (error) {
      console.error('Error signing in:', error);
      toast.error('Error signing in', {
        position: "top-right",
        autoClose: 3000,
      });

    }
  };


  return (
    <Container maxWidth="sm" className={classes.container}>
      <Typography variant="h4" align="center" gutterBottom>
        {isSignUp ? 'Sign Up' : 'Sign In'}
      </Typography>
      {isSignUp ? (
        <SignUpForm onSignUp={handleSignUp} />
      ) : (
        <SignInForm onSignIn={handleSignIn} />
      )}
      <Grid container justify="center">
        <Button
          variant="text"
          className={classes.toggleButton}
          onClick={() => setIsSignUp(!isSignUp)}
        >
          {isSignUp ? 'Already have an account? Sign In' : 'Create an account'}
        </Button>
      </Grid>
    </Container>
  );
};

export default AuthPage;
