import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  return (
    <div className="home-container">
      <header className="home-header">
        <h1>Welcome to the E-commerce App</h1>
        <p>Choose any of the following options</p>
      </header>
      <main className="home-content">
        {/* Existing sections */}
        {/* ... */}

        {/* New sections */}
        <section className="microservice-section">
          <h2>Shipping & Handling</h2>
          <p>Learn about our shipping and handling services.</p>
          <Link to="/shipping" className="btn">Shipping Options</Link>
        </section>
        <section className="microservice-section">
          <h2>Profile Management</h2>
          <p>Manage your profile settings and information.</p>
          <Link to="/profile" className="btn">Manage Profile</Link>
        </section>
        <section className="microservice-section">
          <h2>Contact Us</h2>
          <p>Get in touch with our support team.</p>
          <Link to="/contact" className="btn">Contact Support</Link>
        </section>
      </main>
    </div>
  );
};

export default Home;
