import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './pages/Home';
import ProductList from './pages/ProductList';
import ProductDetail from './pages/ProductDetail';
import AuthPage from './pages/AuthPage';
import UserMenu from './components/UserMenu';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      const storedUser = localStorage.getItem('user');
      console.log(storedUser)

      // Check if storedUser is truthy before parsing
      try {
        if (storedUser) {
          setUser(JSON.parse(storedUser));
        }
      } catch (error) {
        console.error("Error parsing stored user data:", error);
        // Handle the error appropriately (e.g., removing the invalid data from localStorage)
      }      
    }
  }, []);





  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    setUser(null);
  };

  return (
    <Router>
      <div>
        {isAuthenticated && <UserMenu user={user} onLogout={handleLogout} />}
        <Routes>
          <Route
            exact
            path="/"
            element={
              isAuthenticated ? (
                <Home />
              ) : (
                <Navigate to="/auth" />
              )
            }
          />
          <Route
            exact
            path="/products"
            element={
              isAuthenticated ? (
                <ProductList />
              ) : (
                <Navigate to="/auth" />
              )
            }
          />
          <Route
            path="/products/:id"
            element={
              isAuthenticated ? (
                <ProductDetail />
              ) : (
                <Navigate to="/auth" />
              )
            }
          />
          <Route
            path="/auth"
            element={
              isAuthenticated ? (
                <Navigate to="/" />
              ) : (
                <AuthPage setIsAuthenticated={setIsAuthenticated} setUser={setUser} />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
