import React, { useState } from 'react';
import { Container, Typography, Button, Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import SignUpForm from './SignUpForm';
import SignInForm from './SignInForm';

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(4),
  },
  toggleButton: {
    marginTop: theme.spacing(2),
  },
}));

const AuthPage = ({ setIsAuthenticated, setUser }) => {
  const classes = useStyles();
  const [isSignUp, setIsSignUp] = useState(false);

  const handleSignUp = async (formData) => {
    try {
      const response = await fetch('http://localhost:3003/api/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      console.log(data.message);
      // Prompt the user to sign in after successful sign-up
      setIsSignUp(false);
    } catch (error) {
      console.error('Error signing up:', error);
    }
  };

  const handleSignIn = async (formData) => {
    try {
      const response = await fetch('http://localhost:3003/api/signin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      console.log(data.message);
      // Store the token and user object
      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));
      setIsAuthenticated(true);
      setUser(data.user);
      // Redirect to the home page after successful sign-in
      window.location.href = '/';
    } catch (error) {
      console.error('Error signing in:', error);
    }
  };

  return (
    <Container maxWidth="sm" className={classes.container}>
      <Typography variant="h4" align="center" gutterBottom>
        {isSignUp ? 'Sign Up' : 'Sign In'}
      </Typography>
      {isSignUp ? (
        <SignUpForm onSignUp={handleSignUp} />
      ) : (
        <SignInForm onSignIn={handleSignIn} />
      )}
      <Grid container justify="center">
        <Button
          variant="text"
          className={classes.toggleButton}
          onClick={() => setIsSignUp(!isSignUp)}
        >
          {isSignUp ? 'Already have an account? Sign In' : 'Create an account'}
        </Button>
      </Grid>
    </Container>
  );
};

export default AuthPage;
