import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button'; // Add this if you're not already importing Button
import { Link } from 'react-router-dom';
import './ProductList.css';

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
      toast.error('Error accessing Product API', {
        position: "top-right",
        autoClose: 3000,
      });

    }
  };

  // Helper function to format product names into a file path
  const formatImageName = (name) => {
    // If your filenames exactly match your product names (including cases and spaces):
    return `/images/products/${name}.webp`;
  };

  return (
    <div className="plist-product-list-container">
      <div className="plist-header-container">
        {/* Change the Link to Button component with styling to match Inventory */}
        <Button component={Link} to="/" variant="outlined" style={{ marginRight: 'auto' }}>
          &larr; Back to Homepage
        </Button>
        {/* Removed the <h1>Our Product Catalog</h1> and spacer as per your request */}
      </div>
      {products.length === 0 ? (
        <p>No products found.</p>
      ) : (
        <div className="product-grid">
          {products.map((product) => (
            <Link to={`/products/${product.id}`} key={product.id} className="product-card">
              <div className="product-image">
                {/* Use the helper function to dynamically set the image src */}
                <img src={formatImageName(product.name)} alt={product.name} />
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductList;
