import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './ProductList.css';

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Helper function to format product names into a file path
  const formatImageName = (name) => {
    return `/images/products/${name.toLowerCase().replace(/ /g, '_')}.png`;
  };

  return (
    <div className="product-list-container">
      <h1>Our Product Catalog</h1>
      {products.length === 0 ? (
        <p>No products found.</p>
      ) : (
        <div className="product-grid">
          {products.map((product) => (
            <Link to={`/products/${product.id}`} key={product.id} className="product-card">
              <div className="product-image">
                {/* Use the helper function to dynamically set the image src */}
                <img src={formatImageName(product.name)} alt={product.name} />
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductList;
