import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(4),
  },
  table: {
    minWidth: 650,
  },
  shippingFeeCell: {
    fontWeight: 'bold',
    backgroundColor: theme.palette.action.hover,
  },
  description: {
    marginBottom: theme.spacing(4),
  },
}));

const ShippingHandling = () => {
    const classes = useStyles();
    const [shippingInfo, setShippingInfo] = useState('');
    const [products, setProducts] = useState([]);

    useEffect(() => {
      fetchShippingInfo();
      fetchProducts();
    }, []);

  const fetchShippingInfo = async () => {
    try {
      const response = await fetch('http://localhost:8080/shipping-explanation');
      const data = await response.json();
      setShippingInfo(data.explanation);
      console.log('Shipping Info:', data); // For debugging
    } catch (error) {
      console.error('Error fetching shipping info:', error);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:8080/all-shipping-fees');
      const data = await response.json();
      setProducts(data);
      console.log('Products:', data); // For debugging
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  return (
    <div className={classes.container}>
      <Typography variant="h4" gutterBottom>
        Shipping and Handling
      </Typography>
      <Typography variant="body1" className={classes.description}>
        {shippingInfo}
      </Typography>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="shipping fees table">
          <TableHead>
            <TableRow>
              <TableCell>Product ID</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Category</TableCell>
              <TableCell>Shipping Fee</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map((product) => (
              <TableRow key={product.id}>
                <TableCell component="th" scope="row">
                  {product.id}
                </TableCell>
                <TableCell>{product.name || ''}</TableCell>
                <TableCell>{product.description || ''}</TableCell>
                <TableCell>{product.price !== undefined ? `$${product.price.toFixed(2)}` : '-'}</TableCell>
                <TableCell>{product.category || ''}</TableCell>
                <TableCell className={classes.shippingFeeCell}>${product.shippingFee.toFixed(2)}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default ShippingHandling;
