import React, { useState, useEffect } from 'react';
import { Container, Typography, TextField, Button, Grid, Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(6),
  },
  header: {
    position: 'relative',
    display: 'flex',
    alignItems: 'center',
    marginBottom: theme.spacing(4),
  },
  backButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    position: 'absolute',
    left: '50%',
    transform: 'translateX(-50%)',
  },
  form: {
    width: '100%',
    marginTop: theme.spacing(4),
  },
  submitButton: {
    margin: theme.spacing(2, 0),
  },
}));

const Profile = () => {
  const classes = useStyles();
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Retrieve user information from localStorage
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:3003/api/update', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: token,
        },
        body: JSON.stringify(user),
      });
      const data = await response.json();
      console.log(data.message);
      // Update the user information in localStorage
      localStorage.setItem('user', JSON.stringify(data.user));
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  if (!user) {
    return <Typography variant="body1">Loading...</Typography>;
  }

  return (
    <Container maxWidth="sm" className={classes.container}>
      <Box className={classes.header}>
        <Button component={Link} to="/" className={classes.backButton} variant="outlined">
          &larr; Back to Homepage
        </Button>
        <Typography variant="h5" component="h1" className={classes.title}>
          Profile
        </Typography>
      </Box>
      <form className={classes.form} onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField name="firstName" label="First Name" fullWidth value={user.firstName} onChange={handleChange} />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField name="lastName" label="Last Name" fullWidth value={user.lastName} onChange={handleChange} />
          </Grid>
          <Grid item xs={12}>
            <TextField name="address" label="Address" fullWidth value={user.address} onChange={handleChange} />
          </Grid>
          <Grid item xs={12}>
            <TextField name="postalCode" label="Postal Code" fullWidth value={user.postalCode} onChange={handleChange} />
          </Grid>
        </Grid>
        <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submitButton}>
          Save
        </Button>
      </form>
    </Container>
  );
};

export default Profile;
