import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './pages/Home';
import ProductList from './pages/ProductList';
import ProductDetail from './pages/ProductDetail';
import AuthPage from './pages/AuthPage';
import UserMenu from './components/UserMenu';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    // Add an additional check to ensure that 'user' also exists in localStorage before setting isAuthenticated to true
    const storedUser = localStorage.getItem('user');

    if (token && storedUser) {
      try {
        setIsAuthenticated(true);
        setUser(JSON.parse(storedUser));
      } catch (error) {
        console.error("Error parsing stored user data:", error);
        // If there is an error parsing stored user data, consider the authentication as failed
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    setUser(null);
  };

  return (
    <Router>
      <div>
        {isAuthenticated && <UserMenu user={user} onLogout={handleLogout} />}
        <Routes>
          <Route path="/" element={<Navigate replace to="/auth" />} />
          <Route path="/auth" element={!isAuthenticated ? <AuthPage setIsAuthenticated={setIsAuthenticated} setUser={setUser} /> : <Navigate replace to="/" />} />
          <Route path="/home" element={isAuthenticated ? <Home /> : <Navigate replace to="/auth" />} />
          <Route path="/products" element={isAuthenticated ? <ProductList /> : <Navigate replace to="/auth" />} />
          <Route path="/products/:id" element={isAuthenticated ? <ProductDetail /> : <Navigate replace to="/auth" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
