import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  Container,
  Box,
  Avatar,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  // ... existing styles ...
  productImage: {
    width: theme.spacing(8),
    height: theme.spacing(8),
    marginRight: theme.spacing(2),
  },
  inventoryCell: {
    fontWeight: 'bold',
    color: (props) => (props.quantity <= 10 ? theme.palette.error.main : theme.palette.text.primary),
  },
}));

const Inventory = () => {
  const classes = useStyles();
  const [inventory, setInventory] = useState([]);

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const response = await fetch('http://localhost:3002/api/inventory');
      const data = await response.json();
      setInventory(data);
    } catch (error) {
      console.error('Error fetching inventory:', error);
    }
  };

  const getProductImage = (productId) => {
    const product = inventory.find((item) => item.id === productId);
    if (product) {
      return `/images/products/${product.name}.webp`;
    }
    return '';
  };

  return (
    <Container maxWidth="lg" className={classes.container}>
      <Box marginBottom={4}>
        <Typography variant="h4" component="h1" gutterBottom>
          Inventory
        </Typography>
      </Box>
      <TableContainer component={Paper} elevation={3}>
        <Table className={classes.table} aria-label="inventory table">
          <TableHead className={classes.tableHeader}>
            <TableRow>
              <TableCell className={classes.tableHeaderCell}>Product</TableCell>
              <TableCell className={classes.tableHeaderCell}>Name</TableCell>
              <TableCell className={classes.tableHeaderCell}>Description</TableCell>
              <TableCell className={classes.tableHeaderCell}>Price</TableCell>
              <TableCell className={classes.tableHeaderCell}>Category</TableCell>
              <TableCell className={classes.tableHeaderCell}>Inventory</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {inventory.map((item) => (
              <TableRow key={item.id}>
                <TableCell>
                  <Avatar
                    src={getProductImage(item.id)}
                    alt={`Product ${item.id}`}
                    className={classes.productImage}
                  />
                </TableCell>
                <TableCell>{item.name || ''}</TableCell>
                <TableCell>{item.description || ''}</TableCell>
                <TableCell>{item.price !== undefined ? `$${item.price.toFixed(2)}` : '-'}</TableCell>
                <TableCell>{item.category || ''}</TableCell>
                <TableCell className={classes.inventoryCell}>{item.quantity}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default Inventory;
