import React, { useState } from 'react';
import { Container, Typography, TextField, Button, Grid, Paper } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(6),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  paper: {
    padding: theme.spacing(4),
    borderRadius: theme.spacing(1),
    boxShadow: theme.shadows[3],
    maxWidth: 600,
    width: '100%',
  },
  header: {
    marginBottom: theme.spacing(4),
    textAlign: 'center',
  },
  form: {
    width: '100%',
    marginTop: theme.spacing(2),
  },
  backButton: {
    marginBottom: theme.spacing(2),
    alignSelf: 'flex-start',
    marginLeft: -theme.spacing(4),
  },
  submitButton: {
    marginTop: theme.spacing(4),
    width: '100%',
  },
}));

const Contact = () => {
  const classes = useStyles();
  const history = useHistory();
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can send the form data to your backend or perform any necessary actions
    // For simplicity, we'll just simulate a successful submission
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <Container maxWidth="sm" className={classes.container}>
        <Paper className={classes.paper}>
          <Typography variant="h4" component="h1" className={classes.header}>
            Thank you for contacting us!
          </Typography>
          <Typography variant="body1" align="center">
            Your message has been successfully submitted. Our support team will get back to you as soon as possible.
          </Typography>
          <Button
            variant="contained"
            color="primary"
            className={classes.submitButton}
            onClick={() => history.push('/')}
          >
            Back to Home
          </Button>
        </Paper>
      </Container>
    );
  }

  return (
    <Container maxWidth="sm" className={classes.container}>
      <Button
        variant="outlined"
        startIcon={<ArrowBackIcon />}
        className={classes.backButton}
        onClick={() => history.goBack()}
      >
        Back
      </Button>
      <Paper className={classes.paper}>
        <Typography variant="h4" component="h1" className={classes.header}>
          Contact Us
        </Typography>
        <Typography variant="body1" align="center" gutterBottom>
          We're here to help! If you have any questions, concerns, or feedback, please don't hesitate to reach out to us.
          Our dedicated support team is ready to assist you.
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                name="subject"
                label="Subject"
                fullWidth
                value={subject}
                onChange={(e) => setSubject(e.target.value)}
                variant="outlined"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="body"
                label="Message"
                fullWidth
                multiline
                rows={6}
                value={body}
                onChange={(e) => setBody(e.target.value)}
                variant="outlined"
              />
            </Grid>
          </Grid>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            className={classes.submitButton}
          >
            Submit
          </Button>
        </form>
      </Paper>
    </Container>
  );
};

export default Contact;
