import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import './ProductDetail.css';

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/products/${id}`);
      const data = await response.json();
      setProduct(data);
    } catch (error) {
      console.error('Error fetching product:', error);
    }
  };

  // Helper function to format product names into a file path
  const formatImageName = (name) => {
    // Assuming your product names match your image filenames including spaces and case
    return `/images/products/${encodeURIComponent(name)}.webp`;
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="product-detail-container">
      <div className="product-image">
        {/* Use the helper function to dynamically set the image src */}
        <img src={formatImageName(product.name)} alt={product.name} />
      </div>
      <div className="product-info">
        <h2>{product.name}</h2>
        <p className="product-description">{product.description}</p>
        <div className="product-price">${product.price.toFixed(2)}</div>
        <div className="product-category">{product.category}</div>
        <button className="purchase-button">Add to Cart</button>
        <Link to="/products" className="back-button">
          &larr; Back to Products
        </Link>
      </div>
    </div>
  );
};

export default ProductDetail;
